% logistic.mclearN = 2000;A = zeros(N,N);xold = 0.231;maxg = 4000;        % 4000 for good coverage (pause off)ming = 2.9;for gainloop = 1:maxg    g=(4.00-ming)*gainloop/maxg + ming;    for sloop = 1:100			%settle-down loop        xnew = g*xold*(1-xold);        xold = xnew;    end						% end settle-down loop    rep = 10000;    ind = 0;    for rloop = 1:rep        xnew = g*xold*(1-xold);        xold = xnew;        ind = ind + 1;        x(ind) = g + (4.00-ming)*(rloop-1)/(maxg*rep);        y(ind) = xnew;            xx = floor(N*0.999*(g-ming)/(4-ming)) + 1;    yy = floor(N*(1-xnew)) + 1;        A(yy,xx) = A(yy,xx) + 1;    end		% end rloopend		% end gainloopfigure(1)imagesc(A)h = newcolormap('graycolor');caxis([0 20])colormap(h)%colormap(gray)colorbar