% function [y,x] = histfix(vec,numbins,minval,maxval)% y is probability.% x is bin.% Also see histfixplot.mfunction [y,x] = histfix(vec,numbins,minval,maxval)del = (maxval - minval)/numbins;i = 1:numbins;x = minval + i*del - del/2;bin = zeros(1,numbins);maxloop = length(vec);if (maxval - minval)==0    y = zeros(1,numbins);else    for loop = 1:maxloop        index = ceil(numbins*(vec(loop)-minval)/(maxval-minval));        if index <= 0            bin(1) = bin(1) + 1/maxloop;        elseif index >= numbins            bin(numbins) = bin(numbins) + 1/maxloop;        else            bin(index) = bin(index) + 1/maxloop;        end    end    y = bin;end